mapper.update:
UPDATE users
nickname = #{nickname}, email = #{email}, gender = #{gender}, birth_date = #{birthDate}, phone = #{phone}, profile_image = #{profileImage},
password = #{password}
WHERE user_id = #{user_id}

UPDATE users
SET password = #{password}
WHERE user_id = #{userId}

mapper.delete:
DELETE FROM
user_favorites
WHERE
user_id = #{userId}
AND
tmdb_id = #{tmdbId}

mapper.select:
SELECT theater_id
FROM user_theaters
WHERE user_id = #{userId}

mapper.delete:
DELETE FROM user_theaters
WHERE user_id = #{userId}
AND theater_id = #{theaterId}
